select `c`.`name` AS `card_name`,`l`.`nome` AS `location_name`,`l`.`id` AS `location_id`,`c`.`scryfall_id` AS `scryfall_id`,`c`.`image_small` AS `image_small`,`c`.`image_medium` AS `image_medium`,`c`.`image_large` AS `image_large`,`c`.`image_png` AS `image_png`,`c`.`mana_cost` AS `mana_cost`,`c`.`cmc` AS `cmc`,`c`.`type_line` AS `type_line`,`c`.`oracle_text` AS `oracle_text`,`c`.`color_identity` AS `color_identity`,`u`.`username` AS `username`,`u`.`id` AS `user_id` from `megic`.`carta` `c` join `megic`.`local` `l` join `megic`.`usuario_carta` `uc` join `megic`.`usuario` `u` where ((`c`.`scryfall_id` = `uc`.`id_carta`) and (`l`.`id` = `uc`.`id_local`) and (`u`.`id` = `uc`.`id_usuario`))